(function(){
    'uses strict';
    angular.module('externo',['ui.utils.masks','ngAnimate'])
    .controller('adocaoController',['$http',function($http){
        var ctrl            = this;
        ctrl.teste          = 'Minificado man√©';
        ctrl.actual_content = 1;
        ctrl.variable       = 1;
        ctrl.contribuicao = {
            donation: false,
            praying: false,
            working: false
        };


        ctrl.next = function(){
            ctrl.actual_content ++;
            ctrl.variable = 0;
        }

        ctrl.previous = function(){
            ctrl.actual_content =  ctrl.actual_content -1 ;
            ctrl.variable = 0;
        }


        ctrl.toCheck = function(contribuicao){
            console.log(ctrl.contribuicao[contribuicao]);
            ctrl.contribuicao[contribuicao] != ctrl.contribuicao[contribuicao];
        }
    }])
})()
